'use strict';

var gulp = require('gulp');

var convertEncoding = require('../index');

var sourceDir = './source/style-m-detail';
//[sourceDir + '/**/*.js', sourceDir + '/**/*.html', sourceDir + '/**/*.art']
//'./source/style-m-lofty/ui/autocomplete/1.0/autocomplete.js'

gulp.task('convertEncoding', function() {
    return gulp.src([sourceDir + '/**/*.js', sourceDir + '/**/*.html', sourceDir + '/**/*.art'])
                .pipe(convertEncoding('utf-8'))
                .pipe(gulp.dest('./dest/'));
});

gulp.task('default', ['convertEncoding']);
