(function( env ) {
	'use strict';
	
	// lofty configs
	var configs = {
		amd: true,
		resolve: function( id ) {
			var rStyle = /\.css(?:\?|$)/,
                rTrade = /^(amdtest)(\/\w+)+/;

            var type = rStyle.test( id ) ? 'css/' : 'js/';

            if ( rTrade.test( id ) ){
                id = 'app/qingguo/' + type + id;
            }

            return id;
		},
		alias: {
			
		}
	}
	
	if( typeof env.lofty !== 'undefined' ) {
		// for lofty
		env.lofty.config(configs);
	}
	
	if( typeof exports !== 'undefined' && env === exports ) {
		// for node.js
		exports.configs = configs;
	}
	
})(this);