/**
 * !!cmd:parseAmd=true
 */
define(['fui/tabs/1.0', 'amdtest/showtext'], function(Tabs, ShowText) {
	var currentTab = 0,
		intervalSeconds = 2;
	

	var tab = new Tabs({
		tpl:'#staticTab',
		autoPlay: 'line',
		effect: 'updown',
		interval: intervalSeconds * 1000
	});
	
	tab.on('switch', function( data ) {
		currentTab = data.index;
		
		ShowText.show(currentTab);
	});
});