!(function($) {
	/* User配置文件 */
	var config = {
		"searchFormId" : "s_search_form",
		end : 0
	};
	
	var Header = {
			init : function() {
				this.config = config;
				this.searchTabChangeSubmit();
			},
			/**
			 * Tab切换时激发表单提交
			 */
			searchTabChangeSubmit : function() {
				if (!$("#s_searchtabList"))
					return;
		
				var self = this;
				var tabs = $("#s_searchtabList > li > a");
				
				var searchwebUrl ="http://s.1688.com";
				tabs.each(function(){
					$(this).attr("href",searchwebUrl + $(this).attr("action")).removeAttr("action");
				});
				tabs.bind("focus", function() {
					$(this).trigger("blur");
				});
			},
			end : 0
	}

	$(function(){
		Header.init();
	});

})(jQuery);