;(function($){

    var defaultConfig = {
        "navID": "#ali-nav-sub",
        "rootId": "#doc",
        end: 0
    },self;
    
    var NavControl = {

    	init: function() {

            var currentNodeName = $(defaultConfig.rootId).data("nodeName");
            var navWrap = $(defaultConfig.navID);

            if(!navWrap.length){return;}

            $(defaultConfig.navID).find("ul").each(function(i,e){
                if($(e).css('display') === 'block'){
                    var thisNav = $(e);
                    thisNav.children().each(function(i,e){
                        if($(e).text() === currentNodeName){
                            $(e).addClass('current');
                        }
                    });
                }
            });


    	},



    	end: 0
    };

    $(function(){
    	NavControl.init();
    });
})(jQuery);
