(function($){
	var self,QU = Qingguo.Utility;

	function ContentFloat(sb) {
		Sandbox = sb;
		return ContentFloat;
	}

	$.extend(ContentFloat,{
				
		init: function(){

			self = this;

			self.__floatContent();
			
			self.__hiddenContent();
		},

		__floatContent: function() {

			$("#contentfloat").on("mouseenter", function() {
				var target = $(this);
				$("#contenthidden").css("display","block");
			});
			
		},
		
		__hiddenContent: function() {
			
			$("#contenthidden").on("mouseleave", function() {
				var target = $(this);
				target.css("display","none");
			});
		},

		end: 0
	});
	
	Qingguo.Business.ContentFloat = ContentFloat;
	
	AppCore.register("qingguo_mod_contentFloat", Qingguo.Business.ContentFloat);


})(jQuery);