(function($){
	//诚信安全
    function initIntegrity () {
        var container = $('div.mod-integrity');
        
        container.delegate('li', 'mouseenter', function(){
            var that = $(this);
            
            that.addClass('current');
        })
        .delegate('li', 'mouseleave', function(){
            var that = $(this);
            
            that.removeClass('current');
        });
    }
    
    $(function(){
    	initIntegrity();
    });
})(jQuery);
