/**
 * detail Util
 */

define('detail.lib.util.Util', 
        ['jQuery', 'Class'], 
function($, Class) {

    var Util = {
        submit: function(url, data) {
            var $form = $('<form>', {
                    'action': url,
                    'method': 'post'
                }).css('display', 'none');
            
            $.each(data, function(key, value) {
                var $input = $('<input>', {
                    'type': 'hidden',
                    'name': key
                }).val(value);
                $form.append($input);
            });

            $('body').append($form);
            $form.submit();
        }
    };

    return Util;
});
