/**
 * gallery view交互逻辑 
 */
define('detail.modules.gallery.Share', 
        ['jQuery', 'Class', 'lang.Log'], 
function($, Class, Log) {
    var log = new Log('detail.modules.gallery.Share');

return Class({
    
    init: function(view, config) {
        this.onEvent = $.os.supportsTouch ? 'tap' : 'click';
            
        $('a.d-share', view).on('click', function(e){
            e.preventDefault();
        });

        $('a.d-share', view).on('tap', function(e){
            e.preventDefault();
            var elm = $(this);
            
            Wing.navigator.share.share(config.subject);
        });
    }
});


});
