define('detail.modules.offerRemark.Module', ['exports'], function(exports) {


exports.view = function(model) {
	if (!model.rateStarLevelMap) {return};
	model.rateStarLevel45 = model.rateStarLevelMap["4"]-0+(model.rateStarLevelMap["4"]-0);
	model.rateStarLevel3 = model.rateStarLevelMap["3"]-0;
	model.rateStarLevel12 = model.rateStarLevelMap["1"]-0+(model.rateStarLevelMap["2"]-0);

	if(Math.floor(model.rateAverageStarLevel-0) == model.rateAverageStarLevel-0){
		model.starLevel = Math.floor(model.rateAverageStarLevel-0)*10;
	}else{
		model.starLevel = (Math.floor(model.rateAverageStarLevel-0)+0.5)*10;
	}

	for (var i = 0; i < model.rateDsrItems.length; i++) {
		model.rateDsrItems[i].avgScore = (model.rateDsrItems[i].avgScore-0).toFixed(1);
	};

	model.getDirection = function(num){
		if(num>0){
			return "icon-up";
		}else if(num<0){
			return "icon-down";
		}
		return "icon-average";
	}
};


});

