define('detail.modules.offerRemarkList.Module', ['exports'], function(exports) {


exports.view = function(model) {
	if (!model.model) {return};
	for (var i = 0; i < model.model.length; i++) {
		var remark = model.model[i],
			time = remark.gmtPublished.split(" ");

		remark.gmtPublished = time[0];

		if(remark.identityAttr.length>15){
			remark.identityAttr = remark.identityAttr.substring(0,15);
		}

		remark.identityAttr = remark.identityAttr.replace(/#3A/g,' ').replace(/#3B/g,':');
	};

};


});

