define('detail.modules.preferential.Module', ['exports'], function(exports) {


exports.view = function(model) {

    if (!model.promotionDetails){
        return;
    }

    for (var i = 0; i < model.promotionDetails.length; i++){
        var startTime = model.promotionDetails[i].startTime.split(':'),
            endTime = model.promotionDetails[i].endTime.split(':');
        model.promotionDetails[i].startTime = startTime[0] + ':' + startTime[1];
        model.promotionDetails[i].endTime = endTime[0] + ':' + endTime[1];
    }

};


});

